def loss_q_plotter():
    fig_num = 10
    file = 'option_data_new_{}.pkl'.format(fig_num)
    relative_path = '../results'
    with open(os.path.join(relative_path, file), "rb") as f:
        data = pickle.load(f)
    y1 = np.array(data['critic loss'])
    y2 = np.array(data['critic q'])
    x = np.arange(y1.shape[0])

    # fig = plt.figure()
    # ax1 = fig.add_subplot(111)
    # # ax1.set_title('Option Switch')
    # plt.plot(x, y1)
    # y1 = smooth(y1, 0.999)
    # plt.xlabel('Training Episode')
    # plt.ylabel('Critic Loss')
    # plt.yticks([0, 1])
    # plt.legend('Option')

    fig = plt.figure()
    y2 = smooth(y2, 0.999)
    plt.xlabel('Training Episodes')
    plt.ylabel('Critic Q value')
    plt.plot(x, y2)
    plt.legend(['Q value'])
    plt.show()

def smooth(data, factor=0):
    old = 0
    for i, d in enumerate(data):
        data[i] = old * factor + d * (1-factor)
        old = data[i]
    return data



if __name__ == '__main__':
    loss_q_plotter()